import java.util.Collection;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.app.dao.AppDefinitionDao;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;

AppDefinitionDao appDefinitionDao = (AppDefinitionDao) AppUtil.getApplicationContext().getBean("appDefinitionDao");
Collection appDefinitionList = appDefinitionDao.findPublishedApps("name", Boolean.FALSE, null, null);

FormRowSet rows = new FormRowSet();

FormRow emptyRow = new FormRow();
emptyRow.setProperty(FormUtil.PROPERTY_VALUE, "");
emptyRow.setProperty(FormUtil.PROPERTY_LABEL, "");
emptyRow.setProperty(FormUtil.PROPERTY_GROUPING, "");
rows.add(emptyRow);

for (AppDefinition a : appDefinitionList) {
    FormRow r = new FormRow();
    r.setProperty(FormUtil.PROPERTY_VALUE, a.getAppId());
    r.setProperty(FormUtil.PROPERTY_LABEL, a.getName());
    rows.add(r);
}

return rows;

import java.util.Collection;
import org.springframework.util.ClassUtils;
import org.joget.plugin.base.Plugin;
import org.joget.plugin.base.PluginManager;
import org.joget.plugin.base.ApplicationPlugin;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;

PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
Collection list = pluginManager.list(ApplicationPlugin.class);

FormRowSet rows = new FormRowSet();

FormRow emptyRow = new FormRow();
emptyRow.setProperty(FormUtil.PROPERTY_VALUE, "");
emptyRow.setProperty(FormUtil.PROPERTY_LABEL, "");
emptyRow.setProperty(FormUtil.PROPERTY_GROUPING, "");
rows.add(emptyRow);

for (Plugin plugin : list) {
    FormRow r = new FormRow();
    r.setProperty(FormUtil.PROPERTY_VALUE, ClassUtils.getUserClass(plugin).getName());
    r.setProperty(FormUtil.PROPERTY_LABEL, plugin.getI18nLabel());
    rows.add(r);
}

return rows;

